export default function sketch(p) {

    function fetchData(accessToken) {
        return new Promise(async (resolve, reject) => {
            try {
                let results = await
                fetch('https://api.spotify.com/v1/me/player', {
                    headers: {
                        'Authorization': 'Bearer ' + accessToken
                    }
                }).then(response => response.json()).then(data => {
                    let id = data.item.id
                    return id;
                })
                return resolve(results);
            } catch (e) {
                return reject(e);
            }
        })
    }

    function getURLParams(req) {
        var uri = window.location.href;
        var url = new URL(uri);
        var res = url.searchParams.get(req);
        return res
    }

    function getAudioFeatures(id, access_token) {
        return new Promise(async (resolve, reject) => {
            try {
                let fetchURL = 'https://api.spotify.com/v1/audio-features/' + id;
                console.log(fetchURL);
                let results = await
                fetch(fetchURL, {
                    headers: {
                        "Authorization": "Bearer " + access_token
                    }
                }).then(response => response.json()).then(data => {
                    let audioAnalysis = [];
                    audioAnalysis.push({
                        "BPM": data.tempo,
                        "danceability": data.danceability
                    })
                    return audioAnalysis;
                })
                return resolve(results)
            } catch (e) {
                return reject(e);
            }
        })
    }

    async function doEverything() {
        const access_token = await getURLParams("access_token");
        const id = await fetchData(access_token);
        const audioAnalysis = await getAudioFeatures(id, access_token);
    }



    //need to limit rates so i don't make 60 api calls per second
    let rate = 0;
    let test = {}

    //JSON array for song animation values
    var diameter;
    p.setup = function () {
        p.createCanvas(p.windowWidth, p.windowHeight, p.P2D);
        p.colorMode(p.HSB, 360, 100, 100);
        p.noStroke();
        p.ellipseMode(p.RADIUS);
        p.frameRate(600);
    }


    /**
     * draw function should take the audio analysis
     * on every half beat (tatum) change circle radius
     * have like a mild gradient from last radius to current radius 
     * change circle colour every beat
     * change size according to volume
     * beat circle to tempo from API
     */
    p.draw = function () {
        if ((rate % 120) === 0) {
            var response = p.getSongID()
            console.log(response)

        }
        p.clear();
        var r = p.millis() / (1000 / (test.BPM / 60.0));
        r -= p.int(r);
        diameter = p.map(r, 0, 2.5, 400, 0); //map the bpm to diameter, and then change diameter each frame to increase/decrease based on tatums
        // console.log(diameter)
        // *slightly* slow down the seizure-inducing radial gradient effects
        for (var i = 0; i < 7; i++) {
            p.drawGradient(p.windowWidth / 2, p.windowHeight / 2)
        }
        rate++;
    }

    p.getR = function (props) {
        return props.tempo
    }

    p.getDanceability = function (props) {
        return props.danceability
    }

    //makes it look like a speaker driver bouncing around in a weird way
    p.drawGradient = function (x, y) {
        var radius = diameter / 2;
        var randColor = p.random(0, 360);
        for (var r = radius; r > 0; --r) {
            p.fill(200, randColor, 120); //blue
            p.ellipse(x, y, r, r);
            randColor = (randColor + 1) % 69; //tried random numbers until it looked good
        }
    }

    p.getAccessToken = function () {
        var uri = window.location.href;
        var url = new URL(uri);
        var res = url.searchParams.get("access_token");
        return res
    }
    p.getSongID = function () {
        var res = p.getAccessToken()
        return p.loadJSON("https://api.spotify.com/v1/me/player?access_token=" + res, p.getAudioFeatures);

    }

    p.getAudioFeatures = function (data) {
        var res = p.getAccessToken()
        console.log(data)
        var id = data.item.id
        var audioFeatures = p.loadJSON("https://api.spotify.com/v1/audio-features/" + id + '?access_token=' + res, p.resolveAnalysis)
        console.log(audioFeatures)
        var tempo = audioFeatures.tempo;
        var danceability = audioFeatures.danceability;
    }

    p.resolveAnalysis = function(data) {
        var tempo = data.tempo;
        var danceability = data.danceability;
        console.log(tempo, danceability)
    }
}
