import React, { Component } from 'react';
import './App.css';
import Home from './views/Home';
import P5Wrapper from './views/P5Wrapper';
import sketch from './views/js/sketch';

class App extends Component {

  renderP5Component() {
    const auth =  this.state.true;
    console.log(auth)
    let P5WrapperTruthy;

    if(auth) {
      console.log('auth true')
      P5WrapperTruthy = <P5Wrapper sketch={sketch} />
    } else if (!auth) {
      P5WrapperTruthy = <div></div>
    }
  }

  checkIfAuth() {
    var uri = window.location.href;
    var url = new URL(uri);
    if(url.searchParams.get("access_token")) {
      return true;
    } else {
      return false;
    }
  }

  constructor() {
    super();
    this.state = {
        
    }
  }

  componentDidMount() {
    this.state = this.checkIfAuth();
    if(this.state == true) {
      this.state = {
        'true': true
      };
    } else {
      this.state = {
        'true': false
      }
    }
    console.log(this.state)
  }

  render() {
    setInterval(() => this.renderP5Component(), 100)
    return (
    <div>
      <div className="p5Canvas">
        {P5WrapperTruthy}
        {/* <P5Wrapper sketch={sketch} /> */}
      </div>
      <div className="main col-sm-12">
        <Home />
        
      </div>
      </div>
    );
  }
}

export default App;
